document.addEventListener("DOMContentLoaded", function() {var theme = document.querySelector("#theme");theme.addEventListener("click", function() {document.querySelector("html").classList.toggle("light");if (document.querySelector("html").classList.contains("light")) {localStorage.setItem("light", "true");} else {localStorage.setItem("light", "false");}});var styling = document.querySelector("#styling");styling.addEventListener("click", function() {document.querySelector("html").classList.toggle("styling");if (document.querySelector("html").classList.contains("styling")) {localStorage.setItem("styling", "true");} else {localStorage.setItem("styling", "false");}});if (localStorage.getItem("light") == "true") {document.querySelector("html").classList.add("light");}if (localStorage.getItem("styling") == "true") {document.querySelector("html").classList.add("styling");}function showPageLoadTime() {if (window.performance && window.performance.timing) {var timing = window.performance.timing;if (timing.loadEventEnd > 0 && timing.navigationStart > 0) {var domContentLoadedTime = timing.domContentLoadedEventEnd - timing.navigationStart;var loadTime = timing.loadEventEnd - timing.navigationStart;document.querySelector("#dom_time").innerHTML = domContentLoadedTime + " ms";document.querySelector("#load_time").innerHTML = loadTime + " ms";} else {setTimeout(showPageLoadTime, 100);}}}window.addEventListener("load", showPageLoadTime);});